CREATE DATABASE IF NOT EXISTS `mall` DEFAULT CHARSET utf8 COLLATE utf8_general_ci;

USE `mall`;

CREATE TABLE `admin` (
  `id` BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
  -- own column start
  `login_name` VARCHAR(20) NOT NULL,
  `password` VARCHAR(20) NOT NULL,
  -- own column end
  `is_deleted` TINYINT(1) NOT NULL DEFAULT 0,
  `gmt_create` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `gmt_modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- default admin account


CREATE TABLE `category` (
  `id` BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
  -- own column start
  `name` VARCHAR(20) NOT NULL,
  `is_hot` TINYINT(1) NOT NULL DEFAULT 0,
  `product_amount` INT UNSIGNED NOT NULL DEFAULT 0,
  -- own column end
  `is_deleted` TINYINT(1) NOT NULL DEFAULT 0,
  `gmt_create` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `gmt_modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `product` (
  `id` BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
  -- own column start
  `name` VARCHAR(100) NOT NULL,               
  `price` DECIMAL(8,2) NOT NULL DEFAULT 0,    
  `stock` INT UNSIGNED NOT NULL DEFAULT 0,    
  `image_src` VARCHAR(100),
  `brief_intro` TEXT,                         
  `detail_info` TEXT,                         
  `production_date` DATE,                     
  `expiration_date` DATE,                     
  `is_available` TINYINT(1) DEFAULT 1,        
  `is_recommended` TINYINT(1) DEFAULT 0,      
  `category_id` BIGINT(20) UNSIGNED NOT NULL, 
  CONSTRAINT `fk_product_category` FOREIGN KEY (category_id) REFERENCES category(id),
  -- own column end
  `is_deleted` TINYINT(1) NOT NULL DEFAULT 0,
  `gmt_create` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `gmt_modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 33 Duplicate PRIMARY KEY(id) and 36 WARNING of empty `expiration_date`.

CREATE TABLE `user` (
  `id` BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
  -- own column start
  `login_name` VARCHAR(20) NOT NULL,
  `password` VARCHAR(20) NOT NULL,
  `user_name` VARCHAR(20) NOT NULL,
  `sex` TINYINT(1) DEFAULT 1,
  `phone` VARCHAR(20),
  `email` VARCHAR(50),
  `address` VARCHAR(255),
  -- own column end
  `is_deleted` TINYINT(1) NOT NULL DEFAULT 0,
  `gmt_create` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `gmt_modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `order` (
  `id` BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
  -- own column start
  `order_code` BIGINT(20) UNSIGNED NOT NULL,
  `user_id` BIGINT(20) UNSIGNED NOT NULL,
  CONSTRAINT `fk_order_user` FOREIGN KEY (user_id) REFERENCES user(id),
  `receiver` VARCHAR(20),
  `post` INT(6),
  `address` VARCHAR(255),
  `phone` VARCHAR(20),
  `note` VARCHAR(255),
  -- order_status: -1=orderCancal, 0=default waitForPay, 1=waitForDelivery, 2=waitConfirm, 3=orderFinish
  `order_status` TINYINT(1) NOT NULL DEFAULT 0,
  `pay_date` DATETIME,
  `delivery_date` DATETIME,
  `confirm_date` DATETIME,
  -- own column end
  `is_deleted` TINYINT(1) NOT NULL DEFAULT 0,
  `gmt_create` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `gmt_modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `order_item` (
  `id` BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
  -- own column start
  `order_id` BIGINT(20) UNSIGNED,
  `product_id` BIGINT(20) UNSIGNED,
  `user_id` BIGINT(20) UNSIGNED,
  `amount` INT NOT NULL DEFAULT 1,
  CONSTRAINT `fk_order_user` FOREIGN KEY (user_id) REFERENCES user(id),
  CONSTRAINT `fk_order_item_order` FOREIGN KEY (order_id) REFERENCES order(id),
  CONSTRAINT `fk_order_item_product` FOREIGN KEY (product_id) REFERENCES product(id),
  -- own column end
  `is_deleted` TINYINT(1) NOT NULL DEFAULT 0,
  `gmt_create` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `gmt_modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8;